mapper.update:
update grade
set uuid = #{uuid,jdbcType=VARCHAR},
  e_course_id = #{eCourseId,jdbcType=INTEGER},
  stu_id = #{stuId,jdbcType=INTEGER},
  grade = #{grade,jdbcType=DECIMAL}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT ec.uuid uuid, ec.id coureId, ec.start_date strDate, ec.end_date endDate, ec.week week, ec.status status, CONCAT(concat_ws('到',ec.str_time,ec.end_time),'节') claTime, ec.str_time startNode, ec.end_time endNode, c.course_name couName, t.tea_name tName, concat_ws('-',cr.build_name,cr.floor,cr.room_num) location, gd.grade, gd.id gradeId FROM grade gd inner join entity_course ec on ec.id=gd.e_course_id INNER JOIN course c ON ec.course_id = c.id INNER JOIN teacher t ON ec.techer_id = t.tea_num INNER JOIN classroom cr ON ec.cla_room_id = cr.id
gd.stu_id=#{stuId}
and ec.id = #{id}
and c.course_name like concat (concat('%',#{couName}),'%')
and ec.status = #{status}

select

from grade


    stu_id=#{stuId}


    and e_course_id=#{eCourseId}
